package com.youku.player.util;/** * 日志工具类 *  * @author dell *  */public class Logger {	private static final String TAG = "umeidaplayer";	private static boolean DEBUG = false;	public static void setDebugMode(boolean debugMode) {		DEBUG = debugMode;	}	public static void v(String tag, String msg) {		if (DEBUG)			android.util.Log.v(tag, msg == null ? "" : msg);	}	public static void v(String tag, String msg, Throwable tr) {		if (DEBUG)			android.util.Log.v(tag, msg == null ? "" : msg, tr);	}	public static void v(String msg) {		if (DEBUG)			android.util.Log.v(TAG, msg == null ? "" : msg);	}	public static void v(String msg, Throwable tr) {		if (DEBUG)			android.util.Log.v(TAG, msg == null ? "" : msg, tr);	}	public static void d(String tag, String msg) {		if (DEBUG)			android.util.Log.d(tag, msg == null ? "" : msg);	}	public static void d(String tag, String msg, Throwable tr) {		if (DEBUG)			android.util.Log.d(tag, msg == null ? "" : msg, tr);	}	public static void d(String msg) {		if (DEBUG)			android.util.Log.d(TAG, msg == null ? "" : msg);	}	public static void d(String msg, Throwable tr) {		if (DEBUG)			android.util.Log.d(TAG, msg == null ? "" : msg, tr);	}	public static void e(String tag, String msg) {		if (DEBUG)			android.util.Log.e(tag, msg == null ? "" : msg);	}	public static void e(String tag, String msg, Throwable tr) {		if (DEBUG)			android.util.Log.e(tag, msg == null ? "" : msg, tr);	}	public static void e(String msg) {		if (DEBUG)			android.util.Log.e(TAG, msg == null ? "" : msg);	}	public static void e(String msg, Throwable tr) {		if (DEBUG)			android.util.Log.e(TAG, msg == null ? "" : msg, tr);	}}